import React,{useEffect,useState} from 'react';
import searchBank from './list';
import Link from 'next/link';
import styles from './Search.module.css';

function Search(){
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults,setSearchResults] = useState([]);
    const handleChange = event => {
        setSearchTerm(event.target.value);
      };
    useEffect(() => {
        const results = searchBank.filter(item =>
          item.toLowerCase().includes(searchTerm)
        );
        const short = results.length > 5 ? results.slice(0,5) : results;
        setSearchResults(short);
      }, [searchTerm]);
   
      const hula= () => searchTerm.length  < 1 ? 'className.hideStuff' : 'className.list'; 
    
      return (
        <div className={styles.container}>
            <div className={styles.searchContainer}>
             <input className={styles.input}
                    type="text"
                    placeholder="Search"
                    value={searchTerm}
                    onChange={handleChange}
            />
          <ul className={hula}>
             {searchResults.length > 0 ? searchResults.map(item => (  
              <li className={styles.listItem} ><Link href={`/stock?ticker=${item.split('-')[0]}&name=${item.split('-')[1]}`}>{item}</Link></li>
            )) : <p>No item matching search parameter!</p>}
          </ul>
          </div>
        </div>
      );
}
export default Search;
